INSERT INTO `sn_navigation` VALUES (1, 'Webpack', 'Webpack 是当下最热门的前端资源模块化管理和打包工具。它可以将许多松散的模块按照依赖和规则打包成符合生产环境部署的前端资源。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.27/dist/img/webpack.png', '网站', 'https://www.webpackjs.com/');
INSERT INTO `sn_navigation` VALUES (2, 'React', 'React 起源于 Facebook 的内部项目，是一个用于构建用户界面的 JavaScript 库。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.27/dist/img/react.png', '网站', 'https://reactjs.bootcss.com/');
INSERT INTO `sn_navigation` VALUES (3, 'TypeScript', 'TypeScript 是由微软开源的编程语言。它是 JavaScript 的一个超集，而且本质上向这个语言添加了可选的静态类型和基于类的面向对象编程。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.27/dist/img/typescript.png', '网站', 'https://typescript.bootcss.com/');
INSERT INTO `sn_navigation` VALUES (4, 'Svelte', 'Svelte 是构建 Web 应用程序的一种新方法。Svelte 是一个编译器，它将声明性组件转换成高效的 JavaScript 代码，并像做外科手术一样细粒度地更新 DOM。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.27/dist/img/svelte.png', '网站', 'https://www.sveltejs.cn/');
INSERT INTO `sn_navigation` VALUES (5, 'Next.js', 'Next.js 是一个轻量级的 React 服务端渲染应用框架。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.28/dist/img/nextjs.png', '网站', 'https://www.nextjs.cn/');
INSERT INTO `sn_navigation` VALUES (6, 'Babel', 'Babel 是一个 JavaScript 编译器。Babel 通过语法转换器支持最新版本的 JavaScript 语法。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.27/dist/img/babeljs.png', '网站', 'https://www.babeljs.cn/');
INSERT INTO `sn_navigation` VALUES (7, 'Node.js', 'Node.js 是一个基于 Chrome V8 引擎的 JavaScript 运行环境。Node.js 使用了一个事件驱动、非阻塞式 I/O 的模型，使其轻量又高效。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.28/dist/img/nodejs.png', '网站', 'https://www.nodeapp.cn/');
INSERT INTO `sn_navigation` VALUES (8, 'Deno', 'Deno 是一个简单、现代且安全的 JavaScript 和 TypeScript 运行时，deno 基于 V8 引擎并使用 Rust 编程语言构建。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.28/dist/img/deno.png', '网站', 'https://deno.bootcss.com/');
INSERT INTO `sn_navigation` VALUES (9, 'Yarn', 'Yarn 是一个快速、可靠、安全的依赖管理工具。是 NPM 的替代品。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.28/dist/img/yarn.png', '网站', 'https://yarn.bootcss.com/');
INSERT INTO `sn_navigation` VALUES (10, 'Yarn v2', 'Yarn 是一个快速、可靠、安全的依赖管理工具。是 NPM 的替代品。Yarn v2 与 v1 版本有很大的不同，Yarn v2 改进了 CLI 交互、支持 workspace、PnP 等新功能。', 'https://cdn.jsdelivr.net/npm/@bootcss/www.bootcss.com@0.0.28/dist/img/yarn.png', '网站', 'https://www.yarnpkg.com.cn/');
INSERT INTO `sn_navigation` VALUES (11, '前端学习路线', '好好学习，天天敲代码', 'https://objtube.github.io/front-end-roadmap/#/', '学习', 'https://objtube.github.io/front-end-roadmap/#/');
INSERT INTO `sn_navigation` VALUES (12, 'Visual Studio 2019', '功能完备的集成开发环境 (IDE)，适用于 Android、iOS、Windows、Web 和云 (IDE)', 'https://img-prod-cms-rt-microsoft-com.akamaized.net/cms/api/am/imageFileData/RE1Mu3b?ver=5c31', 'NET', 'https://visualstudio.microsoft.com/zh-hans/downloads/');
INSERT INTO `sn_navigation` VALUES (13, 'webpack', '本质上，webpack 是一个现代 JavaScript 应用程序的静态模块打包器(module bundler)。当 webpack 处理应用程序时，它会递归地构建一个依赖关系图(dependency graph)，其中包含应用程序需要的每个模块，然后将所有这些模块打包成一个或多个 bundle。', '1', 'VUE', 'https://www.webpackjs.com/concepts/');
INSERT INTO `sn_navigation` VALUES (14, '现代 JavaScript', '以最新的 JavaScript 标准为基准。通过简单但足够详细的内容，为你讲解从基础到高阶的 JavaScript 相关知识。', 'https://zh.javascript.info/', 'javascript', 'https://zh.javascript.info/');
INSERT INTO `sn_navigation` VALUES (15, 'Sass中文网', 'Sass 是一款强化 CSS 的辅助工具，它在 CSS 语法的基础上增加了变量 (variables)、嵌套 (nested rules)、混合 (mixins)、导入 (inline imports) 等高级功能，这些拓展令 CSS 更加强大与优雅。使用 Sass 以及 Sass 的样式库（如 Compass）有助于更好地组织管理样式文件，以及更高效地开发项目。', 'https://www.sass.hk/docs/', 'CSS框架', 'https://www.sass.hk/docs/');
INSERT INTO `sn_navigation` VALUES (16, 'BootstrapV', '我们开始BootstrapVue之旅游，基于全球最流行的Bootstrap V4框架，构建移动优先的响应式门户，在Vue.js前端栈基础上。', 'http://code.z01.com/bootstrap-vue/docs/', 'VUE', 'http://code.z01.com/bootstrap-vue/docs/');
