INSERT INTO `sn_navigation` VALUES (126, 'AutoHotkey', 'AutoHotkey 是一个自由、开源的宏生成器和自动化软件工具，它让用户能够自动执行重复性任务。AutoHotkey 可以修改任何应用程序的用户界面（例如，把默认的 Windows 按键控制命令替换为 Emacs 风格）。它是由定制的脚本语言驱动，旨在提供键盘快捷键或热键。——wikipedia', '', '工具', 'https://www.autohotkey.com/');
INSERT INTO `sn_navigation` VALUES (127, 'Notepad++', 'Notepad++ 是在微软视窗环境之下的一个免费的代码编辑器。为了产生小巧且有效率的代码编辑器,这个在GPL许可证下的自由软体开发专案采用 win32 api 和 STL 以 ...', '', '工具', 'https://notepad-plus-plus.org/');
