INSERT INTO `sn_navigation` VALUES (130, 'CSS Tricks', '总结一些常用的 CSS 样式\n记录一些 CSS 的新属性和一点奇技淫巧\n在“动”部分下有些动画并不是 CSS 效果，因为没有地方放置，所以暂时寄存在这里\n尽量少说废话，代码简单易用，方便复制', '', 'CSS', 'http://css-tricks.neatbang.com/');
