INSERT INTO `sn_navigation` VALUES (142, '.NET 微服务应用程序架构指南', '本指南介绍如何使用 .NET Core 和 Docker 容器开发基于微服务的应用程序并对其进行管理。', '', 'NET', 'https://docs.microsoft.com/zh-cn/dotnet/architecture/microservices/');
INSERT INTO `sn_navigation` VALUES (143, '微软 eShopOnWeb 开源框架', '基于 ASP.NET Core 构建的单体分层应用架构，使用 DDD 领域驱动设计程序体系结构和部署模型。', '', 'NET', 'https://www.cnblogs.com/MrHSR/p/10855824.html');
INSERT INTO `sn_navigation` VALUES (144, 'IdentityServer4中文文档', 'dentityServer4 是一个免费的开源 OpenID Connect 和 OAuth 2.0 身份认证与授权框架，适用于 ASP.NET Core 平台，IdentityServer4 由 Dominick Baier 和 Brock Allen 两位大神创建和维护，您可以快捷的在应用程序中集成基于令牌的身份验证，单点登录和 API 访问控制，支持非常多的协议实现和可扩展点，IdentityServer4 由 OpenID 基金会正式认证，因此符合规范且可互操作，被微软作为 .NET 基金会项目的一部分，并根据其行为准则运行，虽然这个框架也非常的好，博客也不少，但以下整理的中文文档值得推荐学习。', '', 'NET', 'https://www.xcode.me/post/6038#');
INSERT INTO `sn_navigation` VALUES (145, 'dnSpy基于.NET的反编译工具', 'dnSpy是一款基于.NET的反编译与调试工具，开源免费，能够讲.NET开发的Exe和Dll程序集反编译为C#代码，同时支持断点调试和代码二次编辑，如果您只有编译后的程序集，在没有源码的情况下想还原C#源码，dnSpy绝对是首选。', '', 'NET', 'https://github.com/dnSpy/dnSpy/releases');
INSERT INTO `sn_navigation` VALUES (146, 'Visual Studio 2015', '软就放出了VS2015不同版本的离线安装镜像包，支持32位和64位，现在，您就可以下载并安装它', '', 'NET', 'https://www.xcode.me/post/1916');
INSERT INTO `sn_navigation` VALUES (147, '微软官方常用系统工具合集', '这些小工具原本是为了解决工程师们平常在工作上遇到的各种问题而开发的，之后他们将这些工具集合起来称为 Sysinternals，并免费提供公众下载，其中部分还开源了，一直以来都颇受 IT 界人士的好评。如果把管理员比喻成战士的话，那么 Sysinternals 就是我们手中的良兵利器。熟悉和掌握这些工具，并且对 Windows 的体系有一定的了解，将大幅提高你的电脑维护、应用技能。', '', '工具', 'https://www.xcode.me/post/1631');
