DROP TABLE IF EXISTS `sn_article`;
CREATE TABLE `sn_article`  (
  `article_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题 ',
  `title_text` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容简述',
  `text` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '博客内容',
  `html` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `time_create` date NOT NULL COMMENT '发表时间',
  `time_modified` date NOT NULL COMMENT '修改时间',
  `read` smallint(8) NOT NULL COMMENT '阅读次数',
  `give` smallint(8) NOT NULL COMMENT '点赞',
  `comment` smallint(8) NOT NULL COMMENT '评论',
  `type_title` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '分类标题',
  `url_img` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '图片',
  `label_id` int(11) NOT NULL COMMENT '标签外键',
  `sort_id` int(11) NOT NULL COMMENT '分类外键',
  `user_id` int(5) NOT NULL COMMENT '用户外键id',
  PRIMARY KEY (`article_id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `article_labelsId`(`label_id`) USING BTREE,
  INDEX `article_sortId`(`sort_id`) USING BTREE,
  CONSTRAINT `article_labelsId` FOREIGN KEY (`label_id`) REFERENCES `sn_labels` (`label_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `article_sortId` FOREIGN KEY (`sort_id`) REFERENCES `sn_sort` (`sort_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `article_userId` FOREIGN KEY (`user_id`) REFERENCES `sn_user` (`user_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 247 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

